model.metadata:
CREATE VIEW DOUBLES (CLUST VARCHAR(128) NOT NULL, LN INTEGER NOT NULL, ID VARCHAR(128) NOT NULL, TS TIMESTAMP NOT NULL, OFF LONG, VAL DOUBLE, CONSTRAINT DOUBLES_PK PRIMARY KEY (CLUST,LN,ID,TS,OFF))  OPTIONS (UPDATABLE 'TRUE') AS SELECT A.CLUST, A.LN, A.ID, A.TS, A.OFF, A.VAL FROM TIMESERIES.DOUBLES A;
CREATE VIEW INTEGERS (CLUST VARCHAR(128) NOT NULL, LN INTEGER NOT NULL, ID VARCHAR(128) NOT NULL, TS TIMESTAMP NOT NULL, OFF LONG, VAL INTEGER, CONSTRAINT INTEGERS_PK PRIMARY KEY (CLUST,LN,ID,TS,OFF))  OPTIONS (UPDATABLE 'TRUE') AS SELECT A.CLUST, A.LN, A.ID, A.TS, A.OFF, A.VAL FROM TIMESERIES.INTEGERS A;
CREATE VIEW MASTERDATA (CLUST VARCHAR(128) NOT NULL, LN INTEGER NOT NULL, ID VARCHAR(128) NOT NULL, FREQ VARCHAR(128), DATATYPE VARCHAR(128), INDEXES VARCHAR(4096), STARTTS TIMESTAMP NOT NULL, ENDTS TIMESTAMP NOT NULL, CONSTRAINT MD_PK PRIMARY KEY(CLUST,LN,ID)) OPTIONS (UPDATABLE 'FALSE') AS SELECT A.CLUST, A.LN, A.ID, A.FREQ, A.DATATYPE, A.INDEXES, A.STARTTS, A.ENDTS FROM TIMESERIES.MASTERDATA A;
